body.script:
var _gaq = _gaq || [];
var _overlayDelivered = false;
function showAds() { $("#overlay_ads_style").remove(); }
function hideAds() { return (_overlayDelivered = true); }
RT.ads = RT.ads || {};
function closeOverlay(event) {
$('#overlay_body').trigger("overlayClosed").remove();
showAds();
if (event.preventDefault) event.preventDefault();
RT.ads.overlayVisible = false;
return false;
}
function showOverlay() {
var overlay = $('#overlay_body');
RT.ads.overlayVisible = overlay && overlay.css('display','block') && hideAds();
if(RT.interstitialShown)
RT.interstitialShown.resolve();
}
function showLivingSocial(message) {
$(function() {
hideAds();
$('#livingsocial .deal').html(message);
$('#livingsocial').css('display', 'block');
_gaq.push(['_trackEvent', 'LivingSocial', 'Overlay', 'Showoverlay']);
});
}
function submitLivingSocialEmail() {
var email = $("#lsEmail").attr("value");
if (email == null || email == '' || email == 'Enter your email address...' || email.indexOf('@')
< 0 || email.indexOf('.')
< 0) {
alert("Please enter a valid email address.");
return false;
}
_gaq.push(['_trackEvent', 'LivingSocial', 'Overlay', 'ValidEmailoverlay']);
$('#lsConfirm').hide();
$('#lsEmail').hide();
$('#lsSubmit').hide();
$('#lsClose').show();
$('#lsLink').show();
$('#lsLabel').html('Thanks!
<br />Your email has been submitted.');
$.post('/sponsors/groupon/action/submit-email', $('#lsEmailForm').serialize());
return false;
}

