Comp.GetGlue = Comp.extend({
init: function(element) {
this._super(element);
this.initValues();
var _this = this;
social_init.done(function(){
$.ajax( {
url : '/sponsors/getglue/ajax/points/fetch/',
dataType : "json",
data : { 'since' : '07/18/2012 00:00'  },
context : _this
}).success( function(response, status) {
if (response && response.success) {
this.setState(response);
}
});
});
},
initValues: function() {
var element = this.element;
this.finalScore = element.attr('finalScore') || element.attr("data-finalScore");
this.isRated = false;
this._userPointsAndLevel = null;
},
setState : function(data) {
this._userPointsAndLevel = data;
},
$wts_click: function(e) {
var finalScore = "+";
if ('wts' === this.finalScore || '+' === this.finalScore) {
finalScore = 0;
}
else {
this.rated();
}
this.setScore(finalScore);
},
$star_click: function(e) {
var width = e.currentTarget.clientWidth;
var xpos = e.pageX - this.$star.offset().left;
var finalScore = this.computeScore(width, xpos);
if (finalScore === this.finalScore) {
finalScore = 0; // true toggle
}
else {
this.rated();
}
this.setScore(finalScore);
},
$submit_click: function(e) {
this.rated();
},
setScore : function(finalScore) {
this.finalScore = finalScore;
},
rated : function(finalScore) {
if (false || !this.isRated) {
this.isRated = true;
if (false || (this._userPointsAndLevel != null && this._userPointsAndLevel.reachedNewLevel == true)) {
$("#getGlueLevelUpModal").trigger('showModal', { 'userLevel' : this._userPointsAndLevel.userLevel, 'nextLevel' : this._userPointsAndLevel.nextLevel, 'nextNextLevel' : this._userPointsAndLevel.nextNextLevel });
}
// increment point count
$.ajax( {
url : '/sponsors/getglue/ajax/points/update/',
dataType : 'json',
data : { 'nop' : 0  }
}).success( function(response, status) {
if (response && response.success) {
}
});
}
},
computeScore: function (width, xpos) {
var percent = 100 * (xpos / width);
var finalScore = '0.0';
if (90
< percent) finalScore = '5.0';
else if (80
< percent) finalScore = '4.5';
else if (70
< percent) finalScore = '4.0';
else if (60
< percent) finalScore = '3.5';
else if (50
< percent) finalScore = '3.0';
else if (40
< percent) finalScore = '2.5';
else if (30
< percent) finalScore = '2.0';
else if (20
< percent) finalScore = '1.5';
else if (10
< percent) finalScore = '1.0';
else if (5
< percent) finalScore = '0.5';
return finalScore;
}
});
