function openPopup(movieId, forceFbAuth, mpt, src, tag){
var params = {};
if(movieId)
params["movie"]=movieId;
if(forceFbAuth)
params["forceFbAuth"]=true;
if(mpt)
params["mpt"]=mpt;
if(tag)
params["tag"]=tag;
$("#ogprompt").data("tag", tag);
$("#ogprompt iframe").attr("src", (src ? src : "/msk/popup/splash/" ) + "?" + $.param(params));
$("#ogprompt").dialog("open");
}
function closePopup(){
$("#ogprompt").dialog("close");
}

