function qc_results(result) {
quantSegs="";
for (var i = 0; i
< result.segments.length; i++) {
quantSegs += 'qcs=' + result.segments[i].id;
if (i
<result.segments.length-1) { quantSegs += ';'; }
}
setCookie("qcs",quantSegs,90,"/");
}
